mapper.update:
update book_info
bookName = #{bookname}, bookAuthor = #{bookauthor}, bookPrice = #{bookprice}, bookTypeId = #{booktypeid}, bookDesc = #{bookdesc}, isBorrow = #{isborrowed}, bookImage = #{bookimg}, bookCount = #{bookcount}, inventory = #{inventory},
where bookId = #{bookid}

mapper.select:
select , (select bookTypeName from book_type where book_type.bookTypeId = book_info.bookTypeId) as bookTypeName from book_info
and bookName like concat('%',#{bookname},'%') and bookAuthor like concat('%',#{bookauthor},'%') and bookTypeId = #{booktypeid}
limit #{begin}, #{size}
